-- ----------------------------
-- View structure for activity_stats
-- ----------------------------
DROP VIEW IF EXISTS `activity_stats`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `activity_stats` AS select `a`.`id` AS `id`,`a`.`title` AS `title`,`a`.`status` AS `status`,`a`.`max_participants` AS `max_participants`,`a`.`current_participants` AS `current_participants`,(case when (`a`.`max_participants` > 0) then round(((`a`.`current_participants` / `a`.`max_participants`) * 100),2) else 0 end) AS `registration_rate`,`a`.`created_at` AS `created_at`,`u`.`nickname` AS `creator_name` from (`activities` `a` left join `users` `u` on((`a`.`creator_id` = `u`.`id`)));

-- ----------------------------
-- View structure for user_activity_stats
-- ----------------------------
DROP VIEW IF EXISTS `user_activity_stats`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `user_activity_stats` AS select `u`.`id` AS `user_id`,`u`.`nickname` AS `nickname`,count(`ar`.`id`) AS `total_registrations`,count((case when (`ar`.`status` = 'attended') then 1 end)) AS `attended_count`,count((case when (`ar`.`status` = 'registered') then 1 end)) AS `registered_count` from (`users` `u` left join `activity_registrations` `ar` on((`u`.`id` = `ar`.`user_id`))) where (`u`.`role` = 'user') group by `u`.`id`,`u`.`nickname`;

